/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #117471;
  --color-accent-2: #20bfa9;
  --color-primary: #1d263a;
  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #e1ecf0;

  /* Responsive breakpoints */
  --mobile: 480px;
  --tablet: 768px;
  --desktop: 1024px;
}

/* Root element (use instead of `body`) */
#page {
  font-family: system-ui, sans-serif;
  color: var(--color-primary);
  line-height: 1.2;
  font-size: 16px; /* Base font size for mobile */
  background: white;

  @media (min-width: 768px) {
    font-size: 1.125rem;
  }
}

/* Elements */
.section-container {
  max-width: 100%;
  margin: 0 auto;
  padding: 2rem 1rem; /* Smaller padding on mobile */

  @media (min-width: 768px) {
    max-width: 1200px;
    padding: 5rem 2rem;
  }
}

a.link {
  line-height: 1.3;
  font-weight: 500;
  border-bottom: 2px solid;
  transform: translateY(-2px);
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 1.75rem; /* Smaller heading on mobile */
  line-height: 1.2;
  font-weight: 600;

  @media (min-width: 768px) {
    font-size: 2.5rem;
    line-height: 1.1;
  }
}

.subheading {
  font-weight: 400;
  line-height: 1.4;
  opacity: 90%;
  font-size: 1.5rem;

  @media (min-width: 768px) {
    line-height: 1.5;
    font-size: 1.125rem;
  }
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  color: white;
  font-weight: 500;
  background: var(--color-accent, rebeccapurple);
  border-radius: 3rem;
  padding: 12px 20px; /* Smaller padding on mobile */
  width: 100%; /* Full width on mobile */
  margin: 0.5rem 0;
  transition: var(--transition, 0.1s box-shadow);
  border: 0;

  @media (min-width: 768px) {
    width: auto;
    padding: 15px 25px;
    margin: 0;
  }

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.section .content {
  max-width: 100%;
  margin: 0 auto;
  padding: 2rem 1rem; /* Smaller padding on mobile */

  @media (min-width: 768px) {
    max-width: 1200px;
    padding: 5rem 2rem;
  }

  p {
    padding: 0.25rem 0;
    line-height: 1.4;
    font-size: 1rem;

    @media (min-width: 768px) {
      line-height: 1.5;
    }
  }

  img {
    width: 100%;
    margin: 1.5rem 0; /* Smaller margins on mobile */
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);

    @media (min-width: 768px) {
      margin: 2rem 0;
    }
  }

  h1 {
    font-size: 2rem; /* Smaller on mobile */
    font-weight: 700;
    margin-bottom: 0.75rem;

    @media (min-width: 768px) {
      font-size: 3rem;
      margin-bottom: 1rem;
    }
  }

  h2 {
    font-size: 1.75rem;
    font-weight: 600;
    margin-bottom: 0.5rem;

    @media (min-width: 768px) {
      font-size: 2.25rem;
    }
  }

  h3 {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 0.25rem;

    @media (min-width: 768px) {
      font-size: 1.75rem;
    }
  }

  ul, ol {
    padding: 0.5rem 0;
    padding-left: 1.25rem;
    font-size: 1rem;

