import Siema from "siema";
import Icon from "@iconify/svelte/dist/Icon.svelte";
import { tick } from "svelte";

let slider;
let selector;

// rebuild slider when new items added
$: testimonials, initialize_slider(selector);

let enabled = {
  previous: false,
  next: true,
};
let active_slide = 0;

function slide_previous() {
  slider.prev();
  if (slider.currentSlide === 0) {
    enabled = { previous: false, next: true };
  } else {
    enabled = { previous: true, next: true };
  }
}

function slide_next() {
  slider.next();
  if (slider.currentSlide === testimonials.length - 1) {
    enabled = { previous: true, next: false };
  } else {
    enabled = { previous: true, next: true };
  }
}

async function initialize_slider(selector) {
  if (!selector) return 
  if (slider) slider.destroy(true)
  await tick()
  slider = new Siema({
    selector,
    perPage: {
      800: 2, // 2 items for viewport wider than 800px
      1240: 3, // 3 items for viewport wider than 1240px
    },
    onChange: () => {
      active_slide = slider.currentSlide;
      if (active_slide === 0) {
        enabled = { previous: false, next: true };
      } else if (active_slide === testimonials.length - 1) {
        enabled = { previous: true, next: false };
      } else {
        enabled = { previous: true, next: true };
      }
    },
  });
}

