/* Navigation container */
.section-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.75rem 1rem; /* Smaller padding on mobile */
  background: var(--color-accent);
  width: 100%;
  max-width: 100%;
  position: relative; /* For popup positioning */

  @media (min-width: 768px) {
    padding: 1rem 2rem;
  }
}

/* Logo styles */
.logo {
  font-size: 1.25rem; /* Smaller on mobile */
  font-weight: 600;
  line-height: 1.2;
  width: auto; /* Auto width on mobile */
  max-width: 200px; /* Prevent too wide logos */
  padding: 0.5rem 0;

  @media (min-width: 768px) {
    font-size: 1.7rem;
    width: 15em;
    padding: 1rem 0;
  }
}

/* Desktop navigation */
.desktop-nav {
  display: none; /* Hidden by default on mobile */

  @media (min-width: 800px) {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin-left: 2rem; /* Space from logo */

    nav {
      display: flex;
      align-items: center;
      gap: 2rem; /* Slightly reduced gap */

      @media (min-width: 1024px) {
        gap: 3rem; /* Larger gap on wider screens */
      }

      a.link {
        border-color: transparent;
        transition: border-color 0.1s;
        font-size: 0.95rem;
        color: #fff;
        white-space: nowrap; /* Prevent text wrapping */

        &:hover {
          border-color: initial;
        }
      }

      .button {
        font-size: 0.95rem;
        background-color: var(--color-primary);
        white-space: nowrap;
        padding: 0.75rem 1.25rem; /* Slightly reduced padding */

        @media (min-width: 1024px) {
          font-size: 1rem;
          padding: 0.875rem 1.5rem;
        }
      }
    }
  }
}

/* Mobile navigation */
.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;
  padding: 0 0.5rem; /* Add some padding */

  @media (min-width: 800px) {
    display: none;
  }

  /* Hamburger button styles */
  button {
    padding: 0.5rem;
    background: transparent;
    border: none;
    color: white;
    cursor: pointer;

    &:focus {
      outline: 2px solid rgba(255, 255, 255, 0.5);
      border-radius: 4px;
    }
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.125rem; /* Slightly smaller font */
  display: grid;
  gap: 1rem;
  position: fixed; /* Fixed position instead of absolute */
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  max-height: 90vh; /* Prevent too tall popups */
  overflow-y: auto; /* Allow scrolling if needed */
  padding: 1.5rem; /* Smaller padding on mobile */
  z-index: 999; /* Ensure it's above other content */

  @media (min-width: 480px) {
    font-size: 1.25rem;
    padding: 2rem;
  }

  /* Close button */
  button#close {
    position: absolute;
    right: 0.75rem;
    top: 0.75rem;
    padding: 0.5rem;
