  width: 100%;
  max-width: 100%;
  margin: 0 auto;
  padding: 3rem 1.25rem; /* Smaller padding on mobile */

  @media (min-width: 480px) {
    padding: 4rem 1.5rem;
    gap: 2.5rem;
  }

  @media (min-width: 600px) {
    grid-template-columns: 1fr 2fr;
    gap: 3rem;
    padding: 5rem 2rem;
  }

  @media (min-width: 1024px) {
    gap: 100px;
  }
}

/* Logo container */
.logo {
  width: 100%;
  max-width: 10rem; /* Base size for mobile */
  height: auto;

  @media (min-width: 480px) {
    max-width: 15rem;
  }

  @media (min-width: 600px) {
    width: 20rem;
  }
}

/* Main content area */
.main {
  display: grid;
  place-content: start;
  gap: 1.5rem; /* Smaller gap on mobile */

  @media (min-width: 600px) {
    gap: 2rem;
  }

  .company-description {
    font-weight: 400;
    font-size: 0.9rem; /* Smaller font on mobile */
    line-height: 1.4;
    opacity: 0.8;

    @media (min-width: 480px) {
      font-size: 1rem;
      line-height: 1.4rem;
    }
  }
}

/* Links section */
.links {
  display: grid; /* Stack on mobile */
  gap: 2.5rem;
  width: 100%;

  @media (min-width: 480px) {
    display: flex;
    flex-wrap: wrap;
    gap: 3rem;
    justify-content: flex-start;
  }

  @media (min-width: 600px) {
    justify-content: flex-end;
    gap: 5rem;
  }

  .item {
    display: grid;
    place-content: start;
    min-width: 150px; /* Ensure minimum width for readability */

    .label {
      font-weight: 600;
      font-size: 1rem; /* Smaller on mobile */
      line-height: 1.4;
      opacity: 0.6;
      margin-bottom: 0.75rem;

      @media (min-width: 480px) {
        font-size: 1.125rem;
        line-height: 1.6rem;
        margin-bottom: 1rem;
      }
    }

    nav {
      display: grid;
      place-items: start;
      gap: 0.5rem; /* Smaller gap on mobile */

      @media (min-width: 480px) {
        gap: 0.75rem;
      }
    }
  }
}

/* Navigation links */
nav {
  .link {
    border-color: transparent;
    font-size: 0.9rem; /* Smaller on mobile */
    padding: 0.25rem 0; /* Larger touch target */
    transition: opacity 0.2s ease;

    @media (min-width: 480px) {
      font-size: 1rem;
    }

    &:hover {
      border-color: initial;
      opacity: 0.8;
    }

    /* Improve touch targets on mobile */
    @media (max-width: 480px) {
      display: inline-block;
      padding: 0.5rem 0;
    }
  }
}

/* Subfooter section */
.subfooter {
  border-top: 1px solid var(--border-color);
  padding: 1rem;
  margin-top: 2rem; /* Add space from main footer */

  @media (min-width: 480px) {
    padding-block: 1rem;
    margin-top: 3rem;
  }

  .primo {
    display: block;
    font-weight: 600;
    font-size: 0.7rem; /* Smaller on mobile */
    text-align: center;
    line-height: 1.4;

    @media (min-width: 480px) {
      font-size: 0.75rem;
    }

    a {
      text-decoration: underline;
      color: rgb(53, 217, 148);
      transition: opacity 0.2s ease;

      &:hover {
        opacity: 0.8;
      }

      /* Improve touch targets on mobile */
      @media (max-width: 480px) {
        display: inline-block;
        padding: 0.25rem 0;
      }
    }
  }
}

/* Add spacing utilities for better mobile layout */
.mt-auto {
  margin-top: auto;
}

.mb-auto {
  margin-bottom: auto;
}

/* Optional: Add fade-in animation for footer elements */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.section-container-footer {
  .main, .links, .logo {
    animation: fadeIn 0.5s ease-out forwards;
  }

  .links {
    animation-delay: 0.2s;
  }
}

